
def countOccurance(Text, Pattern):
    count = 0
    for i in range(len(Text)-len(Pattern)+1):
        if Text[i:i+len(Pattern)] == Pattern:
            count = count+1
    return count 

ori=""

pattern="TCT"
print(ori)
print("Number of occurances of %s: %d"%(pattern, countOccurance(ori, pattern)))
